        },
        {
            "Original": "xid 4 Transaction ID, a random number chosen by the client, used by the client and server to associate messages and responses between a client and a server.",
            "Adjusted": "xid 4 transaction ID, a random number chosen by the client, used by the client and server to associate messages and responses between a client and a server."
        },
        {
            "Original": "secs 2 Filled in by client, seconds elapsed since client began address acquisition or renewal process.",
            "Adjusted": "secs 2 filled in by client, seconds elapsed since client began address acquisition or renewal process."
        },
        {
            "Original": "flags 2 Flags (see figure 2).",
            "Adjusted": "flags 2 flags (see figure 2)."
        },
        {
            "Original": "ciaddr 4 Client IP address; only filled in if client is in BOUND, RENEW or REBINDING state and can respond to ARP requests.",
            "Adjusted": "ciaddr 4 client IP address; only filled in if client is in BOUND, RENEW or REBINDING state and can respond to ARP requests."
        },
        {
            "Original": "yiaddr 4 'your' (client) IP address.",
            "Adjusted": "yiaddr 4 'your' client IP address."
        },
        {
            "Original": "siaddr 4 IP address of next server to use in bootstrap; returned in DHCPOFFER, DHCPACK by server.",
            "Adjusted": "siaddr 4 IP address of next server to use in bootstrap; returned in DHCPOFFER, DHCPACK by server."
        },
        {
            "Original": "giaddr 4 Relay agent IP address, used in booting via a relay agent.",
